import { useDispatch, useSelector } from "react-redux";

import { loadProducts, nextPageProducts } from "../../store/products";
import { useEffect } from 'react';

import { useHistory } from 'react-router-dom';

function ProductsPage() {

  const dispatch = useDispatch();
  const history = useHistory();

  const products = useSelector(state => state.products)
  const currPage = useSelector(state => state.products.page);
  const [orderList, setOrderList] = useState([]);

  useEffect(() => {
    dispatch(loadProducts())
  }, []);

  if (!products.Products) return null;

  const handleAddToList = (id) => {
    // if id in list
    if (orderList.find(element => element.id == id)) {
      return console.log('in list already')
    } else {
      setOrderList([...orderList, {id: id, quantity: 1}])
    }
  }

  return (
    <>
    <div className="productsPageContent">
      {products &&
        products.Products.map(product => {

          let quantity = 1;

          return (
            <div className="productBox">
              <p>{product.name}</p>
              <p>{product.stock}</p>
              <button onClick={() => handleAddToList(product.id)}>Add To List</button>
              {/* <form onSubmit={() => handleAddToCart()} className="addToCartForm">
