import { csrfFetch } from './csrf';

const POPULATE_PRODUCTS = "spots/POPULATE_PRODUCTS";

const populateProducts = (products) => {
  return {
    type: POPULATE_PRODUCTS,
    payload: products
  }
}

export const loadProducts = () => async (dispatch) => {
  const response = await csrfFetch('/api/products?page=1&size=10');
  const data = await response.json();
  dispatch(populateProducts(data));
  return response;
}

export const nextPageProducts = () => async (dispatch) => {
  const response = await csrfFetch(`/api/products?page=${pageNum}&size=10`);
  const data = await response.json();
  dispatch(addProducts(data));
  return response
}

const initialState = {};

const productsReducer = ( state = initialState, action) => {
  let newState;
  switch (action.type) {
    case POPULATE_PRODUCTS: {
      newState = {...state};
      newState = action.payload;
      return newState;
    }
    default:
      return state
  }
}

export default productsReducer;
