import { useDispatch, useSelector } from "react-redux";

import { loadProducts, nextPageProducts } from "../../store/products";
import { useEffect, useState } from 'react';

import { useHistory } from 'react-router-dom';

function ProductsPage() {

  const dispatch = useDispatch();
  const history = useHistory();

  const products = useSelector(state => state.products)
  const currPage = useSelector(state => state.products.page);
  const [list, setList] = useState([]);

  let localList = localStorage.getItem("list");

  useEffect(() => {

    dispatch(loadProducts())

    localList = JSON.parse(localList);

    if (localList) setList(localList);

  }, []);

  if (!products.Products) return null;

  const addToList = (product) => {
    let listCopy = [...list];
    let {id} = product;
    let existingProduct = list.find(element => element.id == id)

    // if id in list
    if (existingProduct) {
      return console.log('in list already')
    } else {
      setList([...list, {id: id, quantity: 1}]);
      console.log(list)
    }
  }

  console.log(list)
  return (
    <>
    <div className="productsPageContent">
      {list.map(product => {
        return (
          <>
            <p>id:{product.id} quantity:{product.quantity}</p>
          </>
        );
      })}
      {products &&
        products.Products.map(product => {

          let quantity = 1;

          return (
            <div className="productBox">
              <p>{product.name}</p>
              <p>{product.stock}</p>
              <button onClick={() => addToList(product)}>Add To List</button>
              {/* <form onSubmit={() => handleAddToCart()} className="addToCartForm">
                <label for='quantity'>
                  Quantity
                </label>
