import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useModal } from "../../context/Modal";
import { modifyProduct } from "../../store/products";

function EditProductFormModal({product}) {
  const dispatch = useDispatch();
  const [name , setName] = useState(product.name);
  const [description , setDescription] = useState(product.description);
  const [price , setPrice] = useState(product.price);
  const [stock , setStock] = useState(product.stock);
  const [imageUrl , setImageUrl] = useState(product.imageUrl);
  const [errors, setErrors] = useState([]);
  const { closeModal } = useModal();

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors([]);
    return dispatch(modifyProduct({ id: product.id, name, description, price, stock: parseInt(stock), imageUrl }))
      .then(closeModal)
      .catch(async (res) => {
        const data = await res.json();
        if (data && data.errors) setErrors(data.errors);
    });
  };

  const inputC = "shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
  const labelC = "block text-gray-700 text-sm font-bold mb-2"

  return (
    <>
      <h1 className="px-5 pt-5 mb-2 mt-0 text-3xl font-medium leading-tight text-primary">Edit Product</h1>
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8">
        <label  className="mb-4">
          Name
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            />
        </label>
        <label>
          Description
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            />
        </label>
        <label>
          Price $
          <input
            type="number"
            step='.01'
            value={price}
            precision={2}
            min={'.01'}
            onChange={(e) => setPrice(e.target.value)}
            required
            />
        </label>
        <label>
          Stock
          <input
            type="number"
            value={stock}
            min={1}
            onChange={(e) => setStock(e.target.value)}
            required
            />
        </label>
        <label>
          Image Url
          <input
            type="text"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            required
            />
        </label>
        <ul>
          {errors.map((error, idx) => <li key={idx} className="text-red-500 text-xs italic pb-5">{error}</li>)}
        </ul>
        <button type="submit"  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Edit</button>
      </form>
    </>
  );
}

export default EditProductFormModal;
