import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { useEffect } from "react";
import { showDetailOrder } from "../../store/orders";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";

const OrdersDetails = () => {

  const { orderId } = useParams();
  const dispatch = useDispatch();

  const detailedOrder = useSelector(state => state.orders.detailOrder);

  useEffect(() => {
    dispatch(showDetailOrder(orderId))
  }, [])

  console.log(detailedOrder)

  if (!detailedOrder) return null;

  return (
    <>
      <h1>Order Details</h1>
      <p>{detailedOrder.id}</p>
      <p>{detailedOrder.trackingCompany}</p>
      <p>{detailedOrder.trackingNumber}</p>
      <p>{detailedOrder.status}</p>
      <p>{detailedOrder.createdAt}</p>
      <hr></hr>
      <h2>Products</h2>
      {detailedOrder.Products &&
        detailedOrder.Products.map(product => (
        <>
          <p>{product.name}</p>
          <p>{product.name}</p>
        </>
        ))
      }
    </>
  )
}

export default OrdersDetails;
