import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useModal } from "../../context/Modal";
import { removeProduct } from "../../store/products";
import { loadOrders } from "../../store/orders";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";


function SelectEmptyOrder({productId}) {
  const dispatch = useDispatch();
  const { closeModal } = useModal();
  const history = useHistory();

  const orders = useSelector(state => state.orders);

  useEffect(() => {
    dispatch(loadOrders());
  },[])

  const deleteProduct = () => {
    return closeModal();
  }

  if (!orders.Orders) return null;

  return (
    <div>
      <h1>Select an order to add items to</h1>
      {orders.Orders.map(order =>
        <div onClick={() => history.push(`/orders/${order.id}`)} style={{border: '1px solid black'}}>
          <p>Order: {order.id}</p>
          <p>Tracking Num: {order.trackingNumber}</p>
          <p>{order.status}</p>
        </div>
      )}
    </div>
  )
}

export default SelectEmptyOrder;
