import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { useEffect } from "react";
import { useState } from "react";
import { loadOrders } from "../../store/orders";
import OpenModalButton from "../OpenModalButton";
import CreateOrderModal from "../CreateOrderModal";

const OrdersPage = () => {
  const sessionUser = useSelector((state) => state.session.user);
  const dispatch = useDispatch();
  const history = useHistory();
  const [errors, setErrors] = useState([]);

  const orders = useSelector(state => state.orders);

  useEffect(() => {
    handleLoad();
  },[])

  const handleLoad = (e) => {
    return dispatch(loadOrders())
      .catch(async (res) => {
        const data = await res.json();
        if (!data.ok) console.log(data);
      })
  }

  if (!orders.Orders) return null;

  return (
    <>
    <h3 className="px-5 pt-5 mb-2 mt-0 text-3xl font-medium leading-tight text-primary">Create a new order or select below</h3>
     <OpenModalButton
          buttonText="Create New Order"
          modalComponent={<CreateOrderModal />}
          className={"bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-5"}
        />
     <h3  className="px-5 pt-5 mb-2 mt-0 text-3xl font-medium leading-tight text-primary">Current Orders</h3>
     {orders.Orders.map(order =>
     <div onClick={() => history.push(`/orders/${order.id}`)} style={{border: '1px solid black'}}>
      <p>Order: {order.id}</p>
      <p>Tracking Num: {order.trackingNumber}</p>
      <p>{order.status}</p>
     </div>
      )}
    </>
  )
}

export default OrdersPage;
