import React, { useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../store/session";
import OpenModalButton from "../OpenModalButton";
import { thunkDeleteProject } from "../../store/project";
import EditProjectFormModal from "../EditProjectFormModal";
import { useHistory } from "react-router-dom";
import UsersProjectFormModal from "../UsersProjectFormModal";

function ProductButton({project}) {

  const dispatch = useDispatch();
  const history = useHistory();
  const ulRef = useRef();

  const [showMenu, setShowMenu] = useState(false);

  const projects = useSelector(state => state.projects)
  const user = useSelector(state => state.session.user)

  const openMenu = () => {
    if (showMenu) return;
    setShowMenu(true);
  };

  useEffect(() => {
    if (!showMenu) return;

    const closeMenu = (e) => {
      if (!ulRef.current.contains(e.target)) {
        setShowMenu(false);
      }
    };

    document.addEventListener("click", closeMenu);

    return () => document.removeEventListener("click", closeMenu);
  }, [showMenu]);

  const ulClassName = "projectDropdown" + (showMenu ? " flexCol" : " hidden");
  const closeMenu = () => setShowMenu(false);

  const handleDeleteProject = async () => {
    await dispatch(thunkDeleteProject(project.id))
  }

  return (
    <div>
      <button onClick={openMenu} className="cleanButton"><i className="fa-solid fa-gear fa-xl"/></button>
      <div className={ulClassName} ref={ulRef}>
        {  showMenu &&
        <>
          <OpenModalButton
            buttonText="Manage Members"
            onItemClick={closeMenu}
            className="UsersProjectButton cleanButton"
            modalComponent={<UsersProjectFormModal project={project}/>}
          />
          <OpenModalButton
            buttonText="Edit Project"
            onItemClick={closeMenu}
            className="EditProjectButton cleanButton"
            modalComponent={<EditProjectFormModal project={project}/>}
          />
          <button onClick={handleDeleteProject} className="cleanButton">Delete</button>
        </>
        }
      </div>
    </div>
  )
}

export default ProductButton;
