import { useDispatch, useSelector } from "react-redux";

import { loadProducts, nextPageProducts } from "../../store/products";
import { useEffect } from 'react';

import { useHistory } from 'react-router-dom';

function ProductsPage() {

  const dispatch = useDispatch();
  const history = useHistory();

  const products = useSelector(state => state.products)
  const currPage = useSelector(state => state.products.page);

  useEffect(() => {
    dispatch(loadProducts())
  }, []);

  if (!products.Products) return null;

  const handleAddToCart = () => {

  }

  return (
    <>
    <div className="productsPageContent">
      {products &&
        products.Products.map(product => {

          let quantity = 1;

          return (
            <div className="productBox">
              <p>{product.name}</p>
              <p>{product.stock}</p>
              <form onSubmit={() => handleAddToCart()} className="addToCartForm">
                <label for='quantity'>
                  Quantity
                </label>
                <input
                type="number"
                id='quantity'
                max={product.stock}
                min={1}
                value={quantity}
                onChange={(e) => quantity = e.target.value}
                />
              </form>
            </div>
          )
        })
      }
    </div>
    <button onClick={() => dispatch(nextPageProducts(currPage + 1))}>Load More</button>
    </>
  )
}

export default ProductsPage;
